import Vue from "vue";
import Vuex from "vuex";
import axios from 'axios';

Vue.use(Vuex);

export default new Vuex.Store({
  state: {
    user: null,
    photos: [],
    description: "",
  },
  mutations: {
    setUser(state, user) {
      state.user = user;
    },
    setDescription(state, description) {
      state.description = description;
    },
    setPhotos(state, photos) {
      state.photos = photos;
    }
  },
  actions: {
    async changeQuote(context){
      let response = await axios.get('https://ron-swanson-quotes.herokuapp.com/v2/quotes')
      context.commit('setDescription', response.data[0]);
    },
    async register(context, data) {
      try {
        let response = await axios.post("/api/users", data);
        context.commit('setUser', response.data);
        return "";
      } catch (error) {
        return error.response.data.message;
      }
    },
    async login(context, data) {
      try {
        let response = await axios.post("/api/users/login", data);
        context.commit('setUser', response.data);
        return "";
      } catch (error) {
        return error.response.data.message;
      }
    },
    async logout(context) {
      try {
        await axios.delete("/api/users");
        context.commit('setUser', null);
        return "";
      } catch (error) {
        return error.response.data.message;
      }
    },
    async getUser(context) {
      try {
        let response = await axios.get("/api/users");
        context.commit('setUser', response.data);
        return "";
      } catch (error) {
        return "";
      }
    },
    async upload(context, data) {
      try {
        await axios.post('/api/photos', data);
        return "";
      } catch (error) {
        return error.response.data.message;
      }
    },
    async getMyPhotos(context) {
      try {
        let response = await axios.get("/api/photos");
        context.commit('setPhotos', response.data);
        return "";
      } catch (error) {
        return "";
      }
    },
    async getAllPhotos(context) {
      try {
        let response = await axios.get("/api/photos/all");
        context.commit('setPhotos', response.data);
        return "";
      } catch (error) {
        return "";
      }
    },
    async getOnePhoto(context, id) {
      try {
        let response = await axios.get("/api/photos/" + id);
        context.commit('setPhotos', response.data);
        return "";
      } catch (error) {
        return "";
      }
    },
    async deleteOnePhoto(context, id) {
      try {
        let response = await axios.delete("/api/photos/" + id);
        context.commit('setPhotos', response.data);
        return "";
      } catch (error) {
        return "";
      }
    },
    async editQuote(context, body) {
      try {
        console.log(body.description);
        let response = await axios.put("/api/photos/" + body.id, {
          description: body.description,
        })
        context.commit('setPhotos', response.data);
        return "";
      } catch (error) {
        return "";
      }
    },
  }
})
